import React, { Component } from "react";

class FotoHeader extends Component {

    render() {
        return (
            <header className="foto-header">
                <figure className="foto-usuario">
                    <img src={this.props.foto.urlPerfil} alt="foto do usuario"></img>
                    <figcaption className="foto-usuario">
                        <a href="#">
                            {this.props.foto.loginUsuario}
                        </a>
                    </figcaption>
                </figure>
                <time className="foto-data">{this.props.foto.horario}</time>
            </header>
        );
    }
}

class FotoInfo extends Component {

    render() {
        return (
            <div className="foto-info">
                <div className="foto-info-likes">
                    {
                        this.props.foto.likers.map(likes => {
                            return (
                                <a href="#">{likes.login}, </a>
                            );
                        })
                    }

                    curtiram

                </div>

                <p className="foto-info-legenda">
                    <a className="foto-info-autor">autor </a>
                    {this.props.foto.comentario}
                </p>

                <ul className="foto-info-comentarios">
                    {
                        this.props.foto.comentarios.map(comentario => {
                            return (
                                <li className="comentario">
                                    <a className="foto-info-autor">{comentario.login}</a>
                                    {comentario.texto}
                                </li>
                            );
                        })
                    }

                </ul>
            </div>
        );
    }
}

class FotoAtualizacoes extends Component {

    render() {
        return (
            <section className="fotoAtualizacoes">
                <a href="#" className="fotoAtualizacoes-like">Likar</a>
                <form className="fotoAtualizacoes-form">
                    <input type="text" placeholder="Adicione um comentário..." className="fotoAtualizacoes-form-campo"></input>
                    <input type="submit" value="Comentar!" className="fotoAtualizacoes-form-submit"></input>
                </form>

            </section>
        );
    }
}

export default class FotoItem extends Component {

    render() {
        return (
            <div className="foto">

                <FotoHeader foto={this.props.foto}></FotoHeader>

                <img alt="foto" className="foto-src" src={this.props.foto.urlFoto}></img>

                <FotoInfo foto={this.props.foto} ></FotoInfo>

                <FotoAtualizacoes></FotoAtualizacoes>

            </div>
        );
    }
}
