        fetch()
            .then(response => response.json())
            .then(fotos => {
                this.setState({ fotos: fotos })
            })
    }

    render() {
        return (  
            <div className="fotos container">

                {
                    this.state.fotos.map(foto => <FotoItem foto={foto} key={foto.id} ></FotoItem>)
                }


            </div>
        )
    }
}
