import React, { Component } from "react";
import FotoItem from "./Foto"
import PubSub from "pubsub-js";


export default class TimeLine extends Component {

    constructor(props) {
        super(props);

        this.state = {
            fotos: []
        };
    }

    carregarFotos(props) {
        let urlPerfil;

        if (props.login === undefined) {
            urlPerfil = `https://instalura-api.herokuapp.com/api/fotos?X-AUTH-TOKEN=${localStorage.getItem("auth-token")}`;
        } else {
            urlPerfil = `https://instalura-api.herokuapp.com/api/public/fotos/${props.login}`;
        }

        fetch(urlPerfil)
            .then(response => response.json())
            .then(fotos => {
                this.setState({ fotos: fotos })
            })
    }

    componentDidMount() {
        this.carregarFotos(this.props);

    }

    componentWillReceiveProps(nextProps) {

        if (nextProps.login !== undefined) {
            this.carregarFotos(nextProps);
        }
    }

    componentWillMount() {
        PubSub.subscribe("timeline", function (canal, fotos) {
            this.setState({ fotos: fotos })
        }.bind(this));
    }

    render() {
        return (
            <div className="fotos container">

                {
                    this.state.fotos.map(foto => <FotoItem foto={foto} key={foto.id} ></FotoItem>)
                }


            </div>
        )
    }
}
